mapper.insert:
INSERT INTO study_records (
    user_id, bank_id, total_cards, completed_cards, 
    correct_count, wrong_count, mastery_level, study_duration, 
    last_study_at, created_at, updated_at
) VALUES (
    #{userId}, #{bankId}, #{totalCards}, #{completedCards},
    #{correctCount}, #{wrongCount}, #{masteryLevel}, #{studyDuration},
    #{lastStudyAt}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT * FROM study_records
WHERE user_id = #{userId}
ORDER BY last_study_at DESC

    LIMIT #{limit}
